App({onLaunch:function(){wx.cloud?wx.cloud.init({traceUser:!0}):console.error("请使用 2.2.3 或以上的基础库以使用云能力")},getGoodSList:function(c){wx.cloud.callFunction({name:"getGoodlist",success:function(s){null!=c&&null!=c.success&&c.success(s.result)}})},getGoodSDetail:function(c){wx.cloud.callFunction({name:"getGooddetail",data:{id:c.id},success:function(s){null!=c&&null!=c.success&&c.success(s.result)}})},addShopCart:function(c){wx.cloud.callFunction({name:"addShopcart",data:{data:c.data},success:function(s){0==c.cart&&wx.setStorageSync("ids",[s.result]),c.success()}})},getShopCart:function(c){wx.cloud.callFunction({name:"getShopcart",data:{cart:c.cart,done:c.done},success:function(s){null!=c&&null!=c.success&&c.success(s.result)}})},delShopCart:function(c){wx.cloud.callFunction({name:"delShopcart",data:{ids:c.list},success:function(s){null!=c&&null!=c.success&&c.success()}})},getBillList:function(c){wx.cloud.callFunction({name:"getShopcart",data:{ids:c.ids},success:function(s){null!=c&&null!=c.success&&c.success(s.result)}})},submitorder:function(c){wx.cloud.callFunction({name:"submitShopcart",data:{deliveryType:c.deliveryType,remark:c.remark,addressData:c.addressData,ids:c.ids},success:function(s){c.success()}})},toPayTap:function(c){wx.cloud.callFunction({name:"payShopcart",data:{ids:c.ids},success:function(s){c.success()}})},toDoneOrder:function(c){wx.cloud.callFunction({name:"doneShopcart",data:{ids:c.ids},success:function(s){c.success()}})},delOrderTap:function(c){wx.cloud.callFunction({name:"delShopcart",data:{ids:c.ids},success:function(s){null!=c&&null!=c.success&&c.success()}})}});